%dw 2.0
import try from dw::Runtime
output application/dw

fun getAllstatesFromFile(fileName:String)=
	do {
		var tryResult = try(()-> readUrl("classpath://"++ fileName, "aaplication/xml"))
		--- 
		tryResult match {
			case myoutput if(myoutput.success ~= false) -> myoutput.error
			else -> $.result
		}	
	}

fun getStateDetailsByName(name)=
	do{
		var statesXml = getAllstatesFromFile("states.xml")
		---
		statesXml.states.*state[?($.@name ~= name)].@[0]
	}
---
getAllstatesFromFile("examples/states.xml")

%dw 2.0
import * from dw::Runtime
output application/dw

var fileNameFromQ = attributes.queryParams.file as String  default "states.xml"

fun getAllstatesFromFile(fileName:String)=
	do {
		var tryResult = try(()-> readUrl("classpath://examples/"++ fileName, "aaplication/xml"))
		--- 
		tryResult match {
			case myoutput if(myoutput.success ~= false) -> fail("There are no such file "+ fileName)
			else -> $.result
		}	
	}

fun getStateDetailsByName(name)=
	do{
		var statesXml = getAllstatesFromFile("states.xml")
		---
		statesXml.states.*state[?($.@name ~= name)].@[0]
	}
---
getAllstatesFromFile(fileNameFromQ)
